mapper.select:
SELECT /* com.ceragem.api.crm.dao.CrmMshipCoupnBasDao.selectCoupnInfo */ A.MSHIP_GRADE_CD, A.MSHP_GRADE_CD, B.COUPN_EVENT_BAS_NO , B.EVENT_COMN_CD , B.COUPN_TYPE_CD , B.MSHIP_PLCY_BAS_NO , B.MSHIP_COUPN_BAS_NO , NVL(B.TOT_APPNT_CNT, 0) TOT_APPNT_CNT , NVL(B.DAY_APPNT_CNT, 0) DAY_APPNT_CNT , NVL(B.MONTH_APPNT_CNT, 0) MONTH_APPNT_CNT , C.*, (SELECT COUNT(*) FROM CRM_COUPN_PBLS_HST M WHERE M.RCMDR_CUST_NO = #{rcmdrCustNo2} AND M.PBLS_DIV_CD = B.EVENT_COMN_CD AND M.MSHIP_COUPN_BAS_NO = B.MSHIP_COUPN_BAS_NO AND TO_CHAR(M.REG_DT, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD') ) TODAY_CPN_PLBS_CNT ,(SELECT COUNT(*) FROM CRM_COUPN_PBLS_HST M WHERE M.RCMDR_CUST_NO = #{rcmdrCustNo2} AND M.PBLS_DIV_CD = B.EVENT_COMN_CD AND M.MSHIP_COUPN_BAS_NO = B.MSHIP_COUPN_BAS_NO AND TO_CHAR(M.REG_DT, 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM') ) MONTH_CPN_PLBS_CNT , (SELECT COUNT(*) FROM CRM_COUPN_PBLS_HST M WHERE M.RCMDR_CUST_NO = #{rcmdrCustNo2} AND M.MSHIP_COUPN_BAS_NO = B.MSHIP_COUPN_BAS_NO ) ALL_CPN_PLBS_CNT
, (SELECT /* 더블클릭으로 동일한 데이터 호출 시 - 분까지만 체크 */ COUNT(*) FROM CRM_COUPN_PBLS_HST M WHERE M.ITG_CUST_NO = #{itgCustNo} AND M.MSHP_GRADE_CD = #{mshipGradeCd} AND M.MSHIP_COUPN_BAS_NO = B.MSHIP_COUPN_BAS_NO AND M.PBLS_DIV_CD = B.EVENT_COMN_CD AND TO_CHAR( M.REG_DT, 'YYYYMMDDHH24MI') = TO_CHAR( SYSDATE, 'YYYYMMDDHH24MI')
/* 060 멤버십 회원가입일 경우 2개를 지급해서 체크 안함 */ AND 1=2
) DOUBLE_CLICK_CHK
FROM CRM_MSHIP_PLCY_BAS A LEFT JOIN CRM_MSHIP_APPLY_COUPN_EVENT_REL B ON A.MSHIP_PLCY_BAS_NO = B.MSHIP_PLCY_BAS_NO LEFT JOIN CRM_MSHIP_COUPN_BAS C ON B.MSHIP_COUPN_BAS_NO = C.MSHIP_COUPN_BAS_NO WHERE B.EVENT_COMN_CD = #{coupnTypeCd}
AND A.MSHIP_PLCY_BAS_NO = #{orgMshipPlcyBasNo}
AND A.MSHP_GRADE_CD = #{orgMshipGradeCd}
AND ( A.APPLY_STD_STA_YMD <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND A.APPLY_STD_END_YMD >= TO_CHAR(SYSDATE, 'YYYYMMDD') ) AND ( C.FROM_PBLS_STD_DAY <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND C.TO_PBLS_STD_DAY >= TO_CHAR(SYSDATE, 'YYYYMMDD') )

