mapper.insert:
    
      DECLARE BEGIN 
    
    
                INSERT /* com.ceragem.api.crm.dao.CrmAdvncmtHstDao.insert */ INTO CRM_ADVNCMT_HST (
        
                 ) VALUES (
                      NVL(#{advncmtHstSeq}, FN_CRM_AUTO_SEQ('ADH') )
                     , #{itgCustNo}
                     , #{mshipGradeCd}
                     , NVL(#{storNo}, '141359' )
                     , #{chitNo}
                     , #{buyGodsNo}
                     , #{ordrAmt}
                     , #{applyAmt}
                     , #{payAmt}
                     , #{pblsDivCd}
                     , #{occurAdvncmtScore}
                     , NVL((SELECT SUM(OCCUR_ADVNCMT_SCORE) FROM CRM_ADVNCMT_HST WHERE ITG_CUST_NO =#{itgCustNo} ),0) + NVL(#{occurAdvncmtScore},0)   
                     , #{validPerdStaYmd}
                     , #{validPerdEndYmd}
                     , TO_DATE(#{pblsDt},  'YYYY-MM-DD HH24:MI:SS')
                     , TO_DATE(#{extncDt}, 'YYYY-MM-DD HH24:MI:SS')
                     , #{txn}
                     , NVL(#{pblsChlCd}, 'CRM' ) 
                     , #{cardPblsHstSeq}
                     , #{useTypeCd}
                     , #{dealNo}
                     , #{promNo}
                     , #{campNo}
                     , #{coupnNo}
                     , NULL
                     , NVL(#{amdrId},'CRM')
                     ,  SYSDATE
                     , NVL(#{regrId},'CRM')
                     , SYSDATE
                     , #{regChlCd}
                     , NVL(#{accumYn},'N')
                     , #{rcmdrCustNo2}
                     , #{orgChitNo}
                     , #{rmark}
                 );

            MERGE INTO CRM_ADVNCMT_HST A
            USING ( SELECT * 
                      FROM ( SELECT A.*
                                  , SUM(A.OCCUR_ADVNCMT_SCORE) OVER (PARTITION BY ITG_CUST_NO ORDER BY REG_DT,ADVNCMT_HST_SEQ) SUBSUM
                               FROM CRM_ADVNCMT_HST A 
                              WHERE ITG_CUST_NO = #{itgCustNo}
                               ) A 
                      WHERE SUBSUM != REMAIN_ADVNCMT_SCORE ) B ON ( A.ADVNCMT_HST_SEQ = B.ADVNCMT_HST_SEQ )
             WHEN MATCHED THEN
           UPDATE SET A.REMAIN_ADVNCMT_SCORE = B.SUBSUM;
           
           
           UPDATE  CRM_CUST_BAS 
              SET MSHIP_ADVNCMT_SCORE = NVL((SELECT SUM(OCCUR_ADVNCMT_SCORE) FROM CRM_ADVNCMT_HST WHERE ITG_CUST_NO =#{itgCustNo} ),0) 
                 WHERE ITG_CUST_NO = #{itgCustNo};
                 
        END;

mapper.update:
       UPDATE /* com.ceragem.api.crm.dao.CrmAdvncmtHstDao.update */ CRM_ADVNCMT_HST
          SET ITG_CUST_NO                             =         #{itgCustNo}
            , MSHIP_GRADE_CD                          =         #{mshipGradeCd}
            , STOR_NO                                 =         #{storNo}
            , CHIT_NO                                 =         #{chitNo}
            , BUY_GODS_NO                             =         #{buyGodsNo}
            , ORDR_AMT                                =         #{ordrAmt}
            , APPLY_AMT                               =         #{applyAmt}
            , PAY_AMT                                 =         #{payAmt}
            , PBLS_DIV_CD                             =         #{pblsDivCd}
            , OCCUR_ADVNCMT_SCORE                     =         #{occurAdvncmtScore}
            , REMAIN_ADVNCMT_SCORE                    =         #{remainAdvncmtScore}
            , VALID_PERD_STA_YMD                      =         #{validPerdStaYmd}
            , VALID_PERD_END_YMD                      =         #{validPerdEndYmd}
            , PBLS_DT                                 =         TO_DATE(#{pblsDt},  'YYYY-MM-DD HH24:MI:SS')
            , EXTNC_DT                                =         TO_DATE(#{extncDt}, 'YYYY-MM-DD HH24:MI:SS')
            , TXN                                     =         #{txn}
            , PBLS_CHL_CD                             =         #{pblsChlCd}
            , CARD_PBLS_HST_SEQ                       =         #{cardPblsHstSeq}
            , USE_TYPE_CD                             =         #{useTypeCd}
            , DEAL_NO                                 =         #{dealNo}
            , PROM_NO                                 =         #{promNo}
            , CAMP_NO                                 =         #{campNo}
            , COUPN_NO                                =         #{coupnNo}
            , USE_DT                                  =         #{useDt}
            , AMDR_ID                                 =         #{amdrId}
            , AMD_DT                                  =         SYSDATE
            , REG_CHL_CD                              =         #{regChlCd}
            , ACCUM_YN                                =         NVL(#{accumYn},'N')
