SELECT /*getStmpMarstList*/ A.MSHIP_STMP_BAS_NO FROM CRM_MSHIP_STMP_BAS A JOIN CRM_MSHIP_APPLY_STOR_REL B ON A.MSHIP_STMP_BAS_NO = B.MSHIP_STMP_BAS_NO AND B.STOR_NO = #{storNo} AND A.STMP_TYPE_CD = 0 JOIN CRM_MSHIP_APPLY_CHL_REL C ON B.MSHIP_STMP_BAS_NO = C.MSHIP_STMP_BAS_NO AND C.APPLY_CHL_CD = #{chlCd} JOIN CRM_MSHIP_APPLY_GODS_REL D ON B.MSHIP_STMP_BAS_NO = D.MSHIP_STMP_BAS_NO AND D.GODS_NO IN
#{item}
GROUP BY A.MSHIP_STMP_BAS_NO

SELECT /*getStmpMarstList2*/ A.MSHIP_STMP_BAS_NO ,COUNT(D.GODS_NO) AS CANCEL_CNT FROM CRM_MSHIP_STMP_BAS A JOIN CRM_MSHIP_APPLY_STOR_REL B ON A.MSHIP_STMP_BAS_NO = B.MSHIP_STMP_BAS_NO AND B.STOR_NO = #{storNo} AND A.STMP_TYPE_CD = 0 JOIN CRM_MSHIP_APPLY_CHL_REL C ON B.MSHIP_STMP_BAS_NO = C.MSHIP_STMP_BAS_NO AND C.APPLY_CHL_CD = #{chlCd} JOIN CRM_MSHIP_APPLY_GODS_REL D ON B.MSHIP_STMP_BAS_NO = D.MSHIP_STMP_BAS_NO AND D.GODS_NO IN
#{item}
GROUP BY A.MSHIP_STMP_BAS_NO

SELECT /*getStmpInfoList*/ MSHIP_STMP_BAS_NO , STMP_BAS_NM , STMP_TYPE_CD , STMP_EVENT_CD , FROM_USE_POSS_DT , TO_DATE(TO_CHAR(TO_USE_POSS_DT,'YYYY-MM-DD')||' 23:59:59','YYYY-MM-DD HH24:MI:SS') AS TO_USE_POSS_DT , VALID_YN , VALID_PERD , STMP_MAX_YN , STMP_MAX_CNT , STMP_QNTY , USE_YN , MSHIP_GRADE_CD , MSHP_GRADE_CD , CPRT_CMP_NO , COND_YN , COND_AMT , COUPN_ACCUM_DC_YN , ACCUM_CNT , ORDR_YN , ORDR_AMT , ACCUM_CHL_TXN , BNFIT_CD , POINT_SCORE , COUPN_NO , APPLY_MSHP_GRADE_CTNTS , MSHIP_TYPE_CDS , COND_CNT , DAY_RSTRTN_CNT FROM CRM_MSHIP_STMP_BAS WHERE MSHIP_STMP_BAS_NO IN
#{item}
AND USE_YN = 'Y'

SELECT COUNT(1)
	FROM CRM_STMP_HST
		WHERE MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}
			AND ITG_CUST_NO = #{itgCustNo}

SELECT /*getStmpGodsList*/ * FROM (
SELECT GODS_NO , MIN_BUY_AMT , ITEM_ACCUM_CNT , #{item.ordrAmt} AS ORDR_AMT , #{item.payAmt} AS PAY_AMT , #{item.coupnYn} AS COUPN_YN , #{item.applyYn} AS APPLY_YN , #{item.applyQnty} AS APPLY_QNTY FROM CRM_MSHIP_APPLY_GODS_REL WHERE MSHIP_STMP_BAS_NO = #{mshipStmpBasNo} AND GODS_NO = #{item.buyGodsNo}
)

    	SELECT MAX(STMP_HST_SEQ) AS HST_SEQ FROM 	CRM_STMP_HST
    		WHERE MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}
			        AND ITG_CUST_NO = #{itgCustNo}

