mapper.select:
/*com.ceragem.api.crm.dao.CrmMshipStmpBasDao.getStmpMarstListForEvent*/ SELECT DISTINCT A.MSHIP_STMP_BAS_NO , A.STMP_BAS_NM , A.STMP_TYPE_CD , A.STMP_EVENT_CD , A.FROM_USE_POSS_DT , A.TO_USE_POSS_DT , A.VALID_YN , A.VALID_PERD , A.STMP_MAX_YN , A.STMP_MAX_CNT , A.STMP_QNTY , A.USE_YN , A.MSHIP_GRADE_CD , A.MSHP_GRADE_CD , A.CPRT_CMP_NO , A.COND_YN , A.COND_AMT , A.COUPN_ACCUM_DC_YN , A.ACCUM_CNT , A.ORDR_YN , A.ORDR_AMT , A.ACCUM_CHL_TXN , A.BNFIT_CD , A.POINT_SCORE , A.COUPN_NO , A.DAY_RSTRTN_CNT FROM CRM_MSHIP_STMP_BAS A JOIN CRM_MSHIP_APPLY_STOR_REL B ON A.MSHIP_STMP_BAS_NO = B.MSHIP_STMP_BAS_NO AND A.STMP_TYPE_CD = 1 AND A.USE_YN = 'Y' AND A.ACCUM_CHL_TXN LIKE '%'|| #{chlCd} ||'%'
AND TRUNC(SYSDATE) BETWEEN NVL(FROM_USE_POSS_DT,SYSDATE-1) AND NVL(TO_USE_POSS_DT,SYSDATE+1) AND A.STMP_QNTY > 0
AND A.STMP_EVENT_CD = #{stmpEventCd}
AND B.STOR_NO = #{storNo}
AND A.APPLY_MSHP_GRADE_CTNTS LIKE '%'|| #{mshpGradeCd} ||'%'
AND A.MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}

/* com.ceragem.api.crm.dao.CrmMshipStmpBasDao.getStmpIssueList */ SELECT #{itgCustNo} ITG_CUST_NO , B.MSHIP_STMP_BAS_NO , NVL(A.ISSUE_CNT, NVL( (SELECT MAX(ISSUE_CNT) FROM CRM_STMP_HST HST WHERE HST.ITG_CUST_NO=#{itgCustNo} AND HST.MSHIP_STMP_BAS_NO=B.MSHIP_STMP_BAS_NO ) + 1,0) ) ACCUM_CNT , B.STMP_TYPE_CD , B.STMP_BAS_NM , GREATEST(TO_CHAR(TRUNC(NVL(ACCUM_DT,SYSDATE)),'YYYYMMDD'),NVL(TO_CHAR(B.FROM_USE_POSS_DT,'YYYYMMDD'),'20000101')) FROM_USE_STD_DAY , LEAST(CASE WHEN B.VALID_YN= 'Y' AND B.VALID_PERD > 0 THEN TO_CHAR(TRUNC(NVL(A.ACCUM_DT,SYSDATE)) + B.VALID_PERD -1 ,'YYYYMMDD') ELSE '20991231' END,NVL(TO_CHAR(TO_USE_POSS_DT,'YYYYMMDD'),'20991231')) TO_USE_STD_DAY , B.STMP_MAX_CNT AS STMP_BOARD_CNT , B.STMP_QNTY , CASE WHEN B.COUPN_NO IS NOT NULL THEN '쿠폰' WHEN B.POINT_SCORE IS NOT NULL THEN '포인트' ELSE '-' END ACCUM_TYPE , NVL(STMP_ACCUM_QNTY,0) STMP_ACCUM_QNTY FROM CRM_MSHIP_STMP_BAS B LEFT JOIN ( SELECT A.ISSUE_CNT , A.MSHIP_STMP_BAS_NO , A.ITG_CUST_NO , MIN(ACCUM_DT) ACCUM_DT , MAX(STMP_ACCUM_QNTY) STMP_ACCUM_QNTY FROM CRM_STMP_HST A WHERE A.ITG_CUST_NO = #{itgCustNo} GROUP BY A.ITG_CUST_NO , A.ISSUE_CNT, A.MSHIP_STMP_BAS_NO HAVING MAX(NVL(A.COUPN_NO,A.POINT_SCORE)) IS NULL ) A ON A.MSHIP_STMP_BAS_NO = B.MSHIP_STMP_BAS_NO AND A.ITG_CUST_NO = #{itgCustNo} WHERE TRUNC(SYSDATE) BETWEEN GREATEST(TRUNC(NVL(ACCUM_DT,SYSDATE)),NVL(B.FROM_USE_POSS_DT,TO_DATE('20000101','YYYYMMDD'))) AND LEAST(CASE WHEN B.VALID_YN= 'Y' AND B.VALID_PERD > 0 THEN TRUNC(NVL(A.ACCUM_DT,SYSDATE)) + B.VALID_PERD -1 ELSE TO_DATE('20991231','YYYYMMDD') END,NVL(B.TO_USE_POSS_DT,TO_DATE('20991231','YYYYMMDD')))
AND B.MSHIP_STMP_BAS_NO IN ( SELECT MSHIP_STMP_BAS_NO FROM CRM_MSHIP_APPLY_STOR_REL WHERE STOR_NO = #{storNo} )
AND B.MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}
AND B.MSHIP_STMP_BAS_NO IN
#{item}
AND A.ITG_CUST_NO = #{itgCustNo}
AND B.USE_YN = 'Y' AND (TRUNC(SYSDATE) BETWEEN B.FROM_USE_POSS_DT AND B.TO_USE_POSS_DT)
AND ((TRUNC(SYSDATE) NOT BETWEEN B.FROM_USE_POSS_DT AND B.TO_USE_POSS_DT) OR B.USE_YN = 'N')
AND B.SHOW_YN = 'Y' ORDER BY A.ITG_CUST_NO , B.MSHIP_STMP_BAS_NO

	SELECT * FROM CRM_STMP_HST WHERE STMP_HST_SEQ = (
    	SELECT 
	        MAX(STMP_HST_SEQ) AS STMP_HST_SEQ
	    FROM CRM_STMP_HST 
	        WHERE MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}
	            AND ITG_CUST_NO = #{itgCustNo}
	)

/* com.ceragem.api.crm.dao.CrmMshipStmpBasDao.getStmpDayHist */
	  SELECT COUNT(1)
		FROM CRM_STMP_HST A
       INNER JOIN CRM_MSHIP_STMP_BAS B ON A.MSHIP_STMP_BAS_NO = B.MSHIP_STMP_BAS_NO
	   WHERE A.MSHIP_STMP_BAS_NO = #{mshipStmpBasNo}
		 AND A.ITG_CUST_NO = #{itgCustNo}
		 AND ACCUM_DT BETWEEN TRUNC(SYSDATE) AND TRUNC(SYSDATE+1)
